import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import CreateProperty from './CreateProperty';
import AuthForm from './AuthForm';

function Navbar() {
  const [showProperties, setShowProperties] = useState(false);
  const [showAddProperty, setShowAddProperty] = useState(false);
  const [showAuth, setShowAuth] = useState(false);
  const [isLogin, setIsLogin] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);

  const handleAuthSuccess = (data) => {
    setCurrentUser(data);
    setShowAuth(false);
    if (data.token) {
      localStorage.setItem('token', data.token);
    }
  };

  const handleLogout = () => {
    setCurrentUser(null);
    localStorage.removeItem('token');
  };

  const toggleAuthMode = () => setIsLogin(!isLogin);

  return (
    <nav style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem', backgroundColor: '#f8f9fa' }}>
      <div>
        <button 
          onClick={() => setShowProperties(!showProperties)}
          style={{ padding: '0.5rem 1rem', cursor: 'pointer' }}
        >
          Properties
        </button>
        {showProperties && (
          <div style={{ position: 'absolute', backgroundColor: 'white', padding: '1rem', boxShadow: '0 2px 5px rgba(0,0,0,0.2)' }}>
            <Link to="/properties" style={{ display: 'block', padding: '0.5rem', textDecoration: 'none' }}>View All</Link>
          </div>
        )}
      </div>

      <div>
        {currentUser && (
          <button 
            onClick={() => setShowAddProperty(!showAddProperty)}
            style={{ padding: '0.5rem 1rem', cursor: 'pointer' }}
          >
            Add Property
          </button>
        )}
        {showAddProperty && (
          <div style={{ position: 'absolute', backgroundColor: 'white', padding: '1rem', boxShadow: '0 2px 5px rgba(0,0,0,0.2)' }}>
            <CreateProperty />
          </div>
        )}
      </div>

      <div>
        {currentUser ? (
          <button 
            onClick={handleLogout}
            style={{ padding: '0.5rem 1rem', cursor: 'pointer' }}
          >
            Logout
          </button>
        ) : (
          <>
            <button 
              onClick={() => setShowAuth(!showAuth)}
              style={{ padding: '0.5rem 1rem', cursor: 'pointer' }}
            >
              {isLogin ? 'Login' : 'Sign Up'}
            </button>
            {showAuth && (
              <div style={{ position: 'absolute', right: '0', backgroundColor: 'white', padding: '1rem', boxShadow: '0 2px 5px rgba(0,0,0,0.2)' }}>
                <AuthForm 
                  isLogin={isLogin} 
                  onSuccess={handleAuthSuccess}
                />
                <button 
                  onClick={toggleAuthMode}
                  style={{ 
                    marginTop: '10px', 
                    padding: '0.3rem 0.6rem',
                    fontSize: '0.8rem',
                    cursor: 'pointer'
                  }}
                >
                  Switch to {isLogin ? 'Sign Up' : 'Login'}
                </button>
              </div>
            )}
          </>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
